import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Paper, Typography, List, ListItem, ListItemText, TextField, Button, makeStyles } from '@mui/material';

const useStyles = makeStyles(theme => ({
  paper: {
    padding: theme.spacing(3),
    maxWidth: 400,
    margin: '0 auto',
    marginTop: theme.spacing(2),
  },
  title: {
    marginBottom: theme.spacing(2),
  },
  input: {
    marginBottom: theme.spacing(1),
  },
  button: {
    marginLeft: theme.spacing(1),
  },
}));

const RealTimeStocks = () => {
  const classes = useStyles();
  const [realTimeData, setRealTimeData] = useState([]);
  const [symbol, setSymbol] = useState('MSFT'); // Default stock symbol is MSFT

  const handleSymbolChange = event => {
    setSymbol(event.target.value);
  };

  const fetchStockData = () => {
    // Replace 'YOUR_API_KEY' with your actual Alpha Vantage API key
    const apiKey = 'QQ0OM443G5VF3246';

    // Fetch real-time stock data from the Alpha Vantage API for the latest 30 minutes
    axios
      .get(
        `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${symbol}&interval=1min&apikey=${apiKey}`
      )
      .then(response => {
        const timeSeries = response.data['Time Series (1min)'];

        if (timeSeries) {
          // Convert the fetched data into an array of objects for the latest 30 minutes
          const timestamps = Object.keys(timeSeries).slice(0, 30); // Get the latest 30 timestamps
          const stockData = timestamps.map(timestamp => ({
            timestamp,
            price: timeSeries[timestamp]['1. open'],
          }));

          setRealTimeData(stockData);
        } else {
          setRealTimeData([]);
        }
      })
      .catch(error => {
        console.error(error);
        setRealTimeData([]);
      });
  };

  return (
    <Paper elevation={3} className={classes.paper}>
      <Typography variant="h5" className={classes.title}>Real-Time Stock Prices</Typography>
      <TextField
        label="Enter Stock Symbol"
        value={symbol}
        onChange={handleSymbolChange}
        className={classes.input}
        fullWidth
      />
      <Button variant="contained" color="primary" onClick={fetchStockData} className={classes.button}>
        Watch Stock
      </Button>
      <List>
        {realTimeData.map((stock, index) => (
          <ListItem key={index}>
            <ListItemText
              primary={`${stock.timestamp}:`}
              secondary={`$${stock.price}`}
            />
          </ListItem>
        ))}
      </List>
    </Paper>
  );
};

export default RealTimeStocks;
