import React, { useState } from 'react';
import axios from 'axios';
import { Paper, Typography, TextField, Button, FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const RealTimeStocks = () => {
  const [stockData, setStockData] = useState([]);
  const [symbol, setSymbol] = useState('MSFT');
  const [timeframe, setTimeframe] = useState('1min');

  const handleSymbolChange = event => {
    setSymbol(event.target.value);
  };

  const handleTimeframeChange = event => {
    setTimeframe(event.target.value);
  };

  const fetchStockData = () => {
    const apiKey = 'QQ0OM443G5VF3246';

    let functionName = '';
    let interval = '';
    if (timeframe === '1min') {
      functionName = 'TIME_SERIES_INTRADAY';
      interval = '1min';
    } else if (timeframe === 'daily') {
      functionName = 'TIME_SERIES_DAILY';
      interval = 'daily';
    } else if (timeframe === 'monthly') {
      functionName = 'TIME_SERIES_MONTHLY';
      interval = 'monthly';
    }

    axios
      .get(
        `https://www.alphavantage.co/query?function=${functionName}&symbol=${symbol}&interval=${interval}&apikey=${apiKey}`
      )
      .then(response => {
        const timeSeries = response.data[`Time Series (${interval})`];

        if (timeSeries) {
          const data = Object.keys(timeSeries).map(timestamp => ({
            timestamp,
            price: parseFloat(timeSeries[timestamp]['1. open']),
          }));

          setStockData(data);
        } else {
          setStockData([]);
        }
      })
      .catch(error => {
        console.error(error);
        setStockData([]);
      });
  };

  return (
    <Paper elevation={3} style={{ padding: '20px' }}>
      <Typography variant="h5">Real-Time Stock Prices</Typography>
      <TextField
        label="Enter Stock Symbol"
        value={symbol}
        onChange={handleSymbolChange}
        style={{ marginTop: '10px', marginBottom: '10px' }}
      />
      <FormControl style={{ marginTop: '10px', marginBottom: '10px' }}>
        <InputLabel>Timeframe</InputLabel>
        <Select value={timeframe} onChange={handleTimeframeChange}>
          <MenuItem value="1min">1 Minute</MenuItem>
          <MenuItem value="daily">Daily</MenuItem>
          <MenuItem value="monthly">Monthly</MenuItem>
        </Select>
      </FormControl>
      <Button variant="contained" onClick={fetchStockData}>
        Watch Stock
      </Button>
      {stockData.length > 0 && (
        <LineChart width={600} height={300} data={stockData}>
          <XAxis dataKey="timestamp" />
          <YAxis />
          <CartesianGrid strokeDasharray="3 3" />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="price" stroke="#8884d8" />
        </LineChart>
      )}
    </Paper>
  );
};

export default RealTimeStocks;
