import React, { useState } from 'react';
import axios from 'axios';
import { Paper, Typography, TextField, Button, Alert } from '@mui/material'; // Import Alert from MUI
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, Dot } from 'recharts';

const RealTimeStocks = () => {
  const [realTimeData, setRealTimeData] = useState([]);
  const [symbol, setSymbol] = useState('MSFT'); // Default stock symbol is MSFT
  const [isApiLimited, setIsApiLimited] = useState(false); // Add this state

  const handleSymbolChange = event => {
    setSymbol(event.target.value);
    setIsApiLimited(false); // Reset API limit message when symbol changes
  };

  const fetchStockData = () => {
    const apiKey = 'TSQT8M1RTVEYZ4YH';

    axios
      .get(
        `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${symbol}&interval=60min&apikey=${apiKey}`
      )
      .then(response => {
        const timeSeries = response.data['Time Series (60min)'];

        if (timeSeries) {
          const timestamps = Object.keys(timeSeries);
          const stockData = timestamps.map(timestamp => ({
            timestamp,
            price: parseFloat(timeSeries[timestamp]['1. open']),
          }));

          setRealTimeData(stockData);
          setIsApiLimited(false); // Reset API limit message on successful fetch
        } else {
          setRealTimeData([]);
          setIsApiLimited(true); // Set API limit message
        }
      })
      .catch(error => {
        console.error(error);
        setRealTimeData([]);
        setIsApiLimited(true); // Set API limit message
      });
  };

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Typography variant="h5" gutterBottom>
        Real-Time Stock Prices (Last 24 Hours)
      </Typography>
      {isApiLimited && ( // Display API limit message if true
        <Alert severity="error" sx={{ marginBottom: 2 }}>
          API usage limit reached. Please try again later.
        </Alert>
      )}
      <TextField
        label="Enter Stock Symbol"
        value={symbol}
        onChange={handleSymbolChange}
        fullWidth
        variant="outlined"
        sx={{ marginBottom: 2 }}
      />
      <Button variant="contained" onClick={fetchStockData}>
        Watch Stock
      </Button>
      {realTimeData.length > 0 && (
        <LineChart width={800} height={400} data={realTimeData}>
          {/* Rest of the chart configuration */}
        </LineChart>
      )}
    </Paper>
  );
};

export default RealTimeStocks;
