import React, { useState } from 'react';
import axios from 'axios';
import { Paper, Typography, TextField, Button } from '@mui/material';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';

const RealTimeStocks = () => {
  const [realTimeData, setRealTimeData] = useState([]);
  const [symbol, setSymbol] = useState('MSFT'); // Default stock symbol is MSFT
  const [intervalType, setIntervalType] = useState('60min'); // Default interval type is hourly

  const handleSymbolChange = event => {
    setSymbol(event.target.value);
  };

  const handleIntervalChange = event => {
    setIntervalType(event.target.value);
  };

  const fetchStockData = () => {
    // Replace 'YOUR_API_KEY' with your actual Alpha Vantage API key
    const apiKey = 'YOUR_API_KEY';

    // Calculate the appropriate interval based on the user's choice
    let interval = intervalType;
    if (intervalType === 'daily') {
      interval = '1day';
    } else if (intervalType === 'weekly') {
      interval = '7days';
    }

    // Fetch real-time stock data from the Alpha Vantage API
    axios
      .get(
        `https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=${symbol}&interval=${interval}&apikey=${apiKey}`
      )
      .then(response => {
        const timeSeries = response.data['Time Series'];

        if (timeSeries) {
          const timestamps = Object.keys(timeSeries);
          const stockData = timestamps.map(timestamp => ({
            timestamp,
            price: parseFloat(timeSeries[timestamp]['1. open']),
          }));

          setRealTimeData(stockData);
        } else {
          setRealTimeData([]);
        }
      })
      .catch(error => {
        console.error(error);
        setRealTimeData([]);
      });
  };

  return (
    <Paper elevation={3} style={{ padding: '20px' }}>
      <Typography variant="h5">Real-Time Stock Prices</Typography>
      <TextField
        label="Enter Stock Symbol"
        value={symbol}
        onChange={handleSymbolChange}
        style={{ marginTop: '10px', marginBottom: '10px' }}
      />
      <TextField
        select
        label="Select Interval"
        value={intervalType}
        onChange={handleIntervalChange}
        style={{ marginBottom: '10px' }}
      >
        <option value="60min">Hourly</option>
        <option value="daily">Daily</option>
        <option value="weekly">Weekly</option>
      </TextField>
      <Button variant="contained" onClick={fetchStockData}>
        Watch Stock
      </Button>
      {realTimeData.length > 0 && (
        <LineChart width={800} height={400} data={realTimeData}>
          <XAxis dataKey="timestamp" />
          <YAxis />
          <CartesianGrid strokeDasharray="3 3" />
          <Tooltip />
          <Legend />
          <Line type="monotone" dataKey="price" stroke="#8884d8" />
        </LineChart>
      )}
    </Paper>
  );
};

export default RealTimeStocks;
